\begin{aligned} L(x | \mu, \sigma) &amp;=-\frac{n}{2} \ln (2 \pi)-\frac{n}{2} \ln \left(\sigma^{2}\right)-\frac{1}{2 \sigma^{2}} \sum_{p \in x}(p-\mu)^{2} \\ \frac{d L(x | \mu, \sigma)}{d \mu} &amp;=-0-0-\frac{1}{2 \sigma^{2}} \frac{d L(x | \mu, \sigma)}{d \mu} \sum_{p \in x}(p-\mu)^{2} \\ \frac{d L(x | \mu, \sigma)}{d \mu} &amp;=-\frac{1}{2 \sigma^{2}} \sum_{p \in x}-2(p-\mu) \\ &amp;=\frac{1}{\sigma^{2}} \sum_{p=x}(p-\mu) \end{aligned}

